public class TicTacToeBoard {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.format("%10s%10s","|","|\n");
		System.out.format("%10s%10s","|","|\n");
		System.out.format("%10s%10s","|","|\n");
		System.out.println("----------------------------");		
		System.out.format("%10s%10s","|","|\n");
		System.out.format("%10s%5s%5s","|","X","|\n");
		System.out.format("%10s%10s","|","|\n");
		System.out.println("----------------------------");	
		System.out.format("%10s%10s","|","|\n");
		System.out.format("%10s%10s","|","|\n");
		System.out.format("%10s%10s","|","|\n");

	}

}
